import { useState } from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";

const Login = () => {

    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');

    const handleLoginSubmit = async (e) => {
        e.preventDefault();
        // console.log("login Button");   
        const loginSubmit = {email, password};
        const response = await fetch('http://localhost:4000/login', {
            method: 'POST',
            headers: {'Content-Type' : 'application/json'},
            body: JSON.stringify(loginSubmit)
        }).then(() => {
            console.log("scuccess");
        }).catch((err) => {
            console.log("Error",err);
        })
        const data = await response.json();
        if (data.status === 'OK') {
            alert('Login successful')
        } else {
            alert('Please check your username and password')
        }


    }

    return (

        <div>
            <p>Login</p>
            <div className="home-login">
                <form className="homelogin-form" onSubmit={handleLoginSubmit}>
                    <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Enter Email" required className="homelogin-inputs"></input>
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Enter Password" required className="homelogin-inputs"></input>
                    <button className="homelogin-submit">Login</button>
                </form>
            </div>
        </div>
    );
}

export default Login;
